import React, {useState} from 'react';
import '../Styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios, { all } from 'axios';

// Cloud IEX RESTful API url
const baseURL = 'https://finnhub.io/api/';

function WatchListAPI()
{
  const [symbol, setSymbol] = React.useState("SPY");
  const [assets, setAssets] = React.useState([]);
  const [isSent, setIsSent] = useState(false);

  function handleSubmit() {
    handleSubmit.preventDefault();
    setIsSent(true);  }

  var version = "v1/"
  var type = "quote/"
  //setSymbol("SPY")
  //var symbol = "SPY"

  var getRequest = baseURL + version + type

  React.useEffect(() => {
    axios.get(getRequest,  {
      params: {
        symbol: symbol,
        token: "ckivfdpr01qlj9q7a2rgckivfdpr01qlj9q7a2s0"
      }
    }).then((response) => {
      setAssets(response.data);
      setIsSent(false);
    });
  }, [isSent]);

  if (!assets) return null;

  return (
      <div>
        <p>{symbol}</p>
        <p> ${assets.c}</p>
        <form onSubmit={handleSubmit}>
      <textarea
        placeholder="Symbol"
        value={symbol}
        onChange={e => setSymbol(e.target.value)}
      />
      <button type="submit">Send</button>
    </form>
      </div>
    );
  }

export default WatchListAPI;
