import React from 'react';
import '../Styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function PassiveInvesting(){
  return (
    <div>
        <h1>Passive Investing / Dollar Cost Averaging</h1>
        <p>Dollar cost averaging into large passive index funds is a simple let effective way of getting your fair share of the gains from the stock / bond markets</p>
        <p>If you want to learn more about investing, I highly recommend checking out Investopedia.com
            However, a major benefit of passive investing is that you don't need to know much to get your fair share of returns
        </p>
        <a href="https://www.investopedia.com/terms/p/passiveinvesting.asp">Investopedia: Passive Investing</a>
    </div>
      );
}

export default PassiveInvesting;
